public class M03L11LoopsConditions {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 2.1.1 GE03
		
		int max = 5;
		int sum = 0;
		int total = 0;
		int count = 0;
		
		for (int num = 0; num <= max; num++)
			{sum = num + sum;
			System.out.println(sum);
			}

		while (count <= max)
		{total = count + total;
		System.out.println(total);
		count++;
		
		}

		
	}
	
}
